import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './style.css';
class CreateAccount extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            name: '',
            message: ''
        }; 

    }
    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    };

    handleSubmit = event => {
        event.preventDefault();
        var that = this;
        fetch('http://localhost:8080/api/users', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name: this.state.name,
                email: this.state.email,
                password: this.state.password
            }) 
        })
        .then( (response) => {
            return response.json()
        }).then( (data) => {
            that.setState({...that.state, message: "User created"})
        }).catch(() => this.setState({...that.state, message: 'User not created'}));
    }; 

render() {
    return (
        <div>
        <Link className='linkTo' to="/www">Login</Link>
        <form onSubmit={this.handleSubmit}>
            <label htmlFor="name">Name </label> 
            <input type="text" name="name" placeholder="name" value={this.state.name} onChange={this.handleChange} required/>
            <br/><label htmlFor="email">Email </label> 
            <input type="text" name="email" placeholder="email" value={this.state.email} onChange={this.handleChange} required/>
            <br/>
            <label htmlFor="password">Password </label> 
            <input type="password" name="password" placeholder="Password" value={this.state.password} onChange={this.handleChange} required/>
            <br/>
            <button type="submit">Creat account</button>
            <p>{this.state.message}</p>
        </form>
        </div>
    );
    }
}

   export default CreateAccount; 


